import React, { useState } from "react";

const Button = ({ handleClick, label }) => {
    return <button onClick = { handleClick } > { label } < /button>;
};

const App = () => {
    // save clicks of each button to its own state
    const [good, setGood] = useState(0);
    const [neutral, setNeutral] = useState(0);
    const [bad, setBad] = useState(0);

    const setGoodTo = (vote) => {
        const handler = () => {
            setGood(vote + 1);
        };
        return handler;
    };

    const setNeutralTo = (vote) => {
        const handler = () => {
            setNeutral(vote + 1);
        };
        return handler;
    };

    const setBadTo = (vote) => {
        const handler = () => {
            setBad(vote + 1);
        };
        return handler;
    };

    return ( <
        div >
        <
        h1 > Give Feedback < /h1> <
        Button label = "good"
        handleClick = { setGoodTo(good) }
        /> <
        Button label = "neutral"
        handleClick = { setNeutralTo(good) }
        /> <
        Button label = "bad"
        handleClick = { setBadTo(good) }
        /> <
        h1 > Statistics < /h1> <
        p > { good } < /p> < /
        div >
    );
};

export default App;
