import React, { useState } from "react";

const Button = ({ handleClick, label }) => {
  return <button onClick={handleClick}> {label} </button>;
};

const StatisticsLine = ({ text, value }) => {
  return (
    <p>
      {text} {value}
    </p>
  );
};

const Statistics = ({ allVotes, good, neutral, bad, average, positive }) => {
  if (allVotes === 0) {
    return <h1> No feedback Given </h1>;
  }

  return (
    <div>
      <StatisticsLine text="good" value={good} />
      <StatisticsLine text="neutral" value={neutral} />
      <StatisticsLine text="bad" value={bad} />
      <StatisticsLine text="all" value={allVotes} />
      <StatisticsLine text="average" value={average} />
      <StatisticsLine text="positive" value={positive} />
    </div>
  );
};

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0);
  const [neutral, setNeutral] = useState(0);
  const [bad, setBad] = useState(0);
  const allVotes = bad + good + neutral;
  const average = (bad * -1 + good) / allVotes;
  const positive = (good / allVotes) * 100;

  const setGoodTo = (vote, LaCago) => {
    const handler = () => {
      LaCago(vote + 1);
    };
    return handler;
  };

  const setNeutralTo = (vote) => {
    const handler = () => {
      setNeutral(vote + 1);
    };
    return handler;
  };

  const setBadTo = (vote) => {
    const handler = () => {
      setBad(vote + 1);
    };
    return handler;
  };

  return (
    <div>
      <h1> Give Feedback </h1>
      <Button label="good" handleClick={setGoodTo(good, setGood)} />
      <Button label="neutral" handleClick={setGoodTo(neutral, setNeutral)} />
      <Button label="bad" handleClick={setGoodTo(bad, setBad)} />
      <Statistics
        good={good}
        neutral={neutral}
        bad={bad}
        allVotes={allVotes}
        average={average}
        positive={positive}
      />
    </div>
  );
};

export default App;
