import React from "react";
//Header takes care of rendering the name of the course, Content renders the parts and their number of exercises and Total renders the total number of exercises

const Courses = ({ courses }) => (
  <div>
    {courses.map((course) => (
      <div key={course.id}>
        <Header course={course} />
        <Part course={course} />
      </div>
    ))}
    ;
  </div>
);
const Header = ({ course }) => {
  return <h2> {course.name} </h2>;
};

const Part = ({ course }) => {
  return <h2> {course.key} </h2>;
  //course.parts.map((part) => <p> {part.name + " " + part.exercises} </p>);
};

const Total = ({ course }) => {
  return course.parts.reduce(
    (accumulator, currentValue) => accumulator + currentValue.exercises,
    0
  );
};

const App = () => {
  const courses = [
    {
      name: "Half Stack application development",
      id: 1,
      parts: [
        {
          name: "Fundamentals of React",
          exercises: 10,
          id: 1,
        },
        {
          name: "Using props to pass data",
          exercises: 7,
          id: 2,
        },
        {
          name: "State of a component",
          exercises: 14,
          id: 3,
        },
        {
          name: "Redux",
          exercises: 11,
          id: 4,
        },
      ],
    },
    {
      name: "Node.js",
      id: 2,
      parts: [
        {
          name: "Routing",
          exercises: 3,
          id: 1,
        },
        {
          name: "Middlewares",
          exercises: 7,
          id: 2,
        },
      ],
    },
  ];

  return (
    <div>
      <h1>Web development Curriculum</h1>
      <Courses courses={courses} />
    </div>
  );
};

export default App;
